import Weather from './Weather'
import { useState } from "react";

function Search(){
  const [res,setResult]=useState('')
  const [city, setCity] = useState('')
  const [temp,setTemp] = useState('')
  const [humidity,setHumidity] = useState('')
  const [wind,setWind] = useState('')
  const [show,setShow] = useState(false)
  const CityNamehandlechange = (e)=>{
    setCity(e.target.value)
  } 
  const onClick = async(e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('city', city);

    await fetch('http://localhost:5000/search',{
      method: 'POST',
      body: formData,
    }
    )
     .then(response => response.json())
     .then(data => {
        setResult(data)
        const datastring = JSON.parse(data)
        console.log(typeof(datastring))
        setCity(datastring.location.name)
        setTemp(datastring.current.temp_c)
        setHumitidy(datastring.current.humidity)
        setWind(datastring.current.wind_mph)

        //setShow(true)

    })
     .catch((error) => console.error(error))
  }
  return(
   <div className="form-control">
      <form onSubmit={onClick} >
        <input type="text" name="city" onChange={CityNamehandlechange} />
        <input className='btn' type="submit" value="Search" />
      </form>
      <Weather name={city} temp={temp} humidity={humidity} wind={wind}/>
  </div>
  )
}

export default Search
